"use strict";var HOURHAND=document.querySelector("#hour"),MINUTEHAND=document.querySelector("#minute"),SECONDHAND=document.querySelector("#second");function run_the_clock(){var e=new Date,t=e.getHours(),r=e.getMinutes(),o=e.getSeconds();console.log("Hour: "+t+" Minute: "+r+" Second: "+o);var n=360*t/12+360*r/60/12,c=360*r/60+360*o/60/60,u=360*o/60;HOURHAND.style.transform="rotate("+n+"deg)",MINUTEHAND.style.transform="rotate("+c+"deg)",SECONDHAND.style.transform="rotate("+u+"deg)"}var interval=setInterval(run_the_clock,1e3);